import React, { useEffect, useState } from 'react';
import { X, ArrowRight, TrendingUp, Wallet } from 'lucide-react';

interface TransactionPopupProps {
  opportunity: {
    timestamp: number;
    profitPercentage: number;
    steps: Array<{
      dexName?: string;
      price?: string;
      cost?: string;
      from: string;
      to: string;
    }>;
    gasFee?: number;
    flashLoanAmount?: number;
  };
  onClose: () => void;
  autoCloseDelay?: number;
}

const TransactionPopup: React.FC<TransactionPopupProps> = ({
  opportunity,
  onClose,
  autoCloseDelay = 10000
}) => {
  const [timeLeft, setTimeLeft] = useState(autoCloseDelay);
  const [isClosing, setIsClosing] = useState(false);

  useEffect(() => {
    const startTime = Date.now();
    const interval = setInterval(() => {
      const elapsed = Date.now() - startTime;
      const remaining = Math.max(0, autoCloseDelay - elapsed);
      setTimeLeft(remaining);

      if (remaining === 0) {
        setIsClosing(true);
        setTimeout(onClose, 300); // Wait for fade-out animation
        clearInterval(interval);
      }
    }, 100);

    return () => clearInterval(interval);
  }, [autoCloseDelay, onClose]);

  const progress = (timeLeft / autoCloseDelay) * 100;

  return (
    <div
      className={`
        fixed inset-0 flex items-center justify-center
        bg-black/50 backdrop-blur-sm z-50
        transition-opacity duration-300
        ${isClosing ? 'opacity-0' : 'opacity-100'}
      `}
      onClick={(e) => e.target === e.currentTarget && onClose()}
    >
      <div 
        className={`
          relative bg-gray-800 rounded-lg shadow-xl
          max-w-2xl w-full mx-4 overflow-hidden
          transition-transform duration-300
          ${isClosing ? 'scale-95' : 'scale-100'}
        `}
      >
        {/* Progress Bar */}
        <div 
          className="absolute top-0 left-0 h-1 bg-blue-500 transition-all duration-100"
          style={{ width: `${progress}%` }}
        />

        {/* Header */}
        <div className="flex items-center justify-between p-4 border-b border-gray-700">
          <div className="flex items-center space-x-2">
            <TrendingUp className="w-5 h-5 text-green-400" />
            <h2 className="text-lg font-bold">Arbitrage Opportunity</h2>
          </div>
          <button
            onClick={onClose}
            className="p-1 hover:bg-gray-700 rounded-lg transition-colors"
          >
            <X className="w-5 h-5" />
          </button>
        </div>

        {/* Content */}
        <div className="p-6 space-y-4">
          {/* Profit and Timestamp */}
          <div className="flex justify-between items-center">
            <div>
              <p className="text-sm text-gray-400">Detected at</p>
              <p className="font-medium">
                {new Date(opportunity.timestamp).toLocaleString()}
              </p>
            </div>
            <div className="text-right">
              <p className="text-sm text-gray-400">Expected Profit</p>
              <p className="text-lg font-bold text-green-400">
                +{opportunity.profitPercentage.toFixed(4)}%
              </p>
            </div>
          </div>

          {/* Trading Route */}
          <div className="space-y-2">
            <p className="text-sm text-gray-400">Trading Route</p>
            <div className="space-y-2">
              {opportunity.steps.map((step, index) => (
                <div 
                  key={index}
                  className="flex items-center space-x-2 bg-gray-700/30 p-3 rounded-lg"
                >
                  <div className="flex-1">
                    <p className="font-medium">{step.from}</p>
                    <p className="text-sm text-gray-400">{step.dexName || 'Unknown DEX'}</p>
                  </div>
                  <ArrowRight className="w-4 h-4 text-gray-400" />
                  <div className="flex-1 text-right">
                    <p className="font-medium">{step.to}</p>
                    <p className="text-sm text-gray-400">
                      Price: {step.price || 'N/A'}
                    </p>
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Additional Details */}
          <div className="grid grid-cols-2 gap-4">
            <div className="bg-gray-700/30 p-3 rounded-lg">
              <p className="text-sm text-gray-400">Gas Fee</p>
              <p className="font-medium">
                {opportunity.gasFee 
                  ? `${opportunity.gasFee} MATIC`
                  : 'N/A'
                }
              </p>
            </div>
            <div className="bg-gray-700/30 p-3 rounded-lg">
              <p className="text-sm text-gray-400">Flash Loan Amount</p>
              <p className="font-medium">
                {opportunity.flashLoanAmount 
                  ? `${opportunity.flashLoanAmount.toFixed(2)} USDC`
                  : 'N/A'
                }
              </p>
            </div>
          </div>
        </div>

        {/* Footer */}
        <div className="p-4 bg-gray-700/30 flex justify-between items-center">
          <p className="text-sm text-gray-400">
            Auto-closing in {Math.ceil(timeLeft / 1000)}s
          </p>
          <button
            className="px-4 py-2 bg-blue-500 text-white rounded-lg
                     hover:bg-blue-600 transition-colors flex items-center space-x-2"
          >
            <Wallet className="w-4 h-4" />
            <span>Execute Trade</span>
          </button>
        </div>
      </div>
    </div>
  );
};

export default TransactionPopup;
