import React from 'react';
import { WifiOff, Wifi, RefreshCw } from 'lucide-react';

interface ConnectionStatusProps {
  isConnected: boolean;
  reconnectAttempts: number;
  maxReconnectAttempts: number;
  onManualReconnect: () => void;
  lastUpdate: Date;
}

const ConnectionStatus: React.FC<ConnectionStatusProps> = ({
  isConnected,
  reconnectAttempts,
  maxReconnectAttempts,
  onManualReconnect,
  lastUpdate
}) => {
  const isReconnecting = reconnectAttempts > 0;

  return (
    <div className="flex items-center space-x-4">
      <div 
        className={`
          relative flex items-center space-x-2 px-3 py-1.5 rounded-full 
          ${isConnected 
            ? 'bg-green-500/10 text-green-400' 
            : 'bg-red-500/10 text-red-400'
          }
          ${isReconnecting ? 'animate-pulse' : ''}
        `}
      >
        {isConnected ? (
          <>
            <Wifi className="w-4 h-4" />
            <span className="text-sm font-medium">Connected</span>
          </>
        ) : (
          <>
            <WifiOff className="w-4 h-4" />
            <span className="text-sm font-medium">
              {isReconnecting 
                ? `Reconnecting (${reconnectAttempts}/${maxReconnectAttempts})`
                : 'Disconnected'
              }
            </span>
          </>
        )}

        {/* Status Indicator Dot */}
        <div 
          className={`
            absolute -right-1 -top-1 h-3 w-3 rounded-full
            ${isConnected ? 'bg-green-500' : 'bg-red-500'}
            ${isConnected && 'animate-ping'}
          `}
        />
      </div>

      {/* Last Update */}
      <div className="flex items-center space-x-2 text-sm text-gray-400">
        <RefreshCw 
          className={`w-4 h-4 ${isConnected ? 'animate-spin' : ''}`} 
        />
        <span>
          Last update: {lastUpdate.toLocaleTimeString()}
        </span>
      </div>

      {/* Manual Reconnect Button */}
      {!isConnected && reconnectAttempts >= maxReconnectAttempts && (
        <button
          onClick={onManualReconnect}
          className={`
            flex items-center space-x-1 px-3 py-1.5 
            text-sm font-medium rounded-md
            bg-blue-500/20 text-blue-400 
            hover:bg-blue-500/30 transition-colors
          `}
        >
          <RefreshCw className="w-4 h-4" />
          <span>Retry Connection</span>
        </button>
      )}
    </div>
  );
};

export default ConnectionStatus;
