import React from 'react';
import { useTable, useSortBy } from 'react-table';
import { ArbitrageOpportunity } from '../types';
import { format } from 'date-fns';
import { ptBR } from 'date-fns/locale';
import numeral from 'numeral';
import {
  ArrowUpIcon,
  ArrowDownIcon,
  ArrowRightIcon,
} from '@heroicons/react/24/solid';

interface ArbitrageTableProps {
  opportunities: ArbitrageOpportunity[];
}

function ArbitrageTable({ opportunities }: ArbitrageTableProps) {
  const data = React.useMemo(() => opportunities || [], [opportunities]);

  const columns = React.useMemo(() => [
    {
      Header: 'Rota',
      accessor: (row: ArbitrageOpportunity) => row.route || '',
      id: 'route',
      Cell: ({ value }: { value: string }) => (
        <div className="font-medium text-gray-900">{value}</div>
      ),
    },
    {
      Header: 'Lucro',
      accessor: (row: ArbitrageOpportunity) => row.profit || 0,
      id: 'profit',
      sortType: 'number',
      Cell: ({ value }: { value: number }) => (
        <div className={`font-medium ${value > 0 ? 'text-green-600' : 'text-red-600'}`}>
          {numeral(value).format('$0,0.00')}
        </div>
      ),
    },
    {
      Header: 'DEXs',
      accessor: (row: ArbitrageOpportunity) => {
        if (!row.steps || !Array.isArray(row.steps)) return '';
        return row.steps.map(step => step?.dex || '').join(' → ');
      },
      id: 'dexs',
      Cell: ({ value }: { value: string }) => (
        <div className="text-sm text-gray-600 flex items-center space-x-1">
          {value.split(' → ').map((dex: string, index: number, arr: string[]) => (
            <React.Fragment key={index}>
              <span className="px-2 py-1 bg-gray-100 rounded">{dex}</span>
              {index < arr.length - 1 && (
                <ArrowRightIcon className="h-4 w-4 text-gray-400" />
              )}
            </React.Fragment>
          ))}
        </div>
      ),
    },
    {
      Header: 'Flash Loan',
      accessor: (row: ArbitrageOpportunity) => row.flashLoanAmount || 0,
      id: 'flashLoanAmount',
      sortType: 'number',
      Cell: ({ value }: { value: number }) => (
        <div className="text-gray-900">
          {numeral(value).format('0,0.00')}
        </div>
      ),
    },
    {
      Header: 'Lucro %',
      accessor: (row: ArbitrageOpportunity) => row.profitPercentage || 0,
      id: 'profitPercentage',
      sortType: 'number',
      Cell: ({ value }: { value: number }) => (
        <div className={`font-medium ${value > 0 ? 'text-green-600' : 'text-red-600'}`}>
          {numeral(value / 100).format('0.00%')}
        </div>
      ),
    },
    {
      Header: 'Data/Hora',
      accessor: (row: ArbitrageOpportunity) => row.timestamp || Date.now(),
      id: 'timestamp',
      sortType: 'datetime',
      Cell: ({ value }: { value: number }) => (
        <div className="text-sm text-gray-600">
          {format(new Date(value), "dd/MM/yyyy HH:mm:ss", { locale: ptBR })}
        </div>
      ),
    },
  ], []);

  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    rows,
    prepareRow,
  } = useTable(
    {
      columns,
      data,
      initialState: {
        sortBy: [{ id: 'profitPercentage', desc: true }],
      } as any,
    },
    useSortBy
  );

  if (!Array.isArray(opportunities) || opportunities.length === 0) {
    return (
      <div className="text-center py-8 text-gray-500">
        Nenhuma oportunidade de arbitragem encontrada
      </div>
    );
  }

  return (
    <div className="overflow-x-auto">
      <table
        {...getTableProps()}
        className="min-w-full divide-y divide-gray-200 table-auto"
      >
        <thead className="bg-gray-50">
          {headerGroups.map(headerGroup => (
            <tr {...headerGroup.getHeaderGroupProps()}>
              {headerGroup.headers.map((column: any) => (
                <th
                  {...column.getHeaderProps(column.getSortByToggleProps())}
                  className={`
                    px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider
                    ${column.isSorted ? 'bg-gray-100' : ''}
                    cursor-pointer hover:bg-gray-100 transition-colors
                  `}
                >
                  <div className="flex items-center space-x-1">
                    <span>{column.render('Header')}</span>
                    <span className="inline-flex flex-col h-4 justify-center">
                      {column.isSorted && (
                        column.isSortedDesc ? (
                          <ArrowDownIcon className="h-4 w-4 text-blue-500" />
                        ) : (
                          <ArrowUpIcon className="h-4 w-4 text-blue-500" />
                        )
                      )}
                    </span>
                  </div>
                </th>
              ))}
            </tr>
          ))}
        </thead>
        <tbody
          {...getTableBodyProps()}
          className="bg-white divide-y divide-gray-200"
        >
          {rows.map(row => {
            prepareRow(row);
            return (
              <tr
                {...row.getRowProps()}
                className="hover:bg-gray-50 transition-colors"
              >
                {row.cells.map(cell => (
                  <td
                    {...cell.getCellProps()}
                    className="px-6 py-4 whitespace-nowrap"
                  >
                    {cell.render('Cell')}
                  </td>
                ))}
              </tr>
            );
          })}
        </tbody>
      </table>
    </div>
  );
}

export default ArbitrageTable;
