import React, { useEffect, useState } from 'react';
import LoadingSpinner from './LoadingSpinner';

interface TokenCardProps {
  symbol: string;
  address: string;
  decimals: number;
  isActive: boolean;
  lastOpportunity?: {
    profit: number;
    timestamp: number;
  };
}

const TokenCard: React.FC<TokenCardProps> = ({
  symbol,
  address,
  decimals,
  isActive,
  lastOpportunity
}) => {
  const [isUpdating, setIsUpdating] = useState(false);
  const [showProfit, setShowProfit] = useState(false);

  // Animate when new opportunity is detected
  useEffect(() => {
    if (lastOpportunity) {
      setIsUpdating(true);
      setShowProfit(true);
      const timer = setTimeout(() => {
        setIsUpdating(false);
      }, 1000);
      return () => clearTimeout(timer);
    }
  }, [lastOpportunity?.timestamp]);

  // Auto-hide profit after delay
  useEffect(() => {
    if (showProfit) {
      const timer = setTimeout(() => {
        setShowProfit(false);
      }, 5000);
      return () => clearTimeout(timer);
    }
  }, [showProfit]);

  return (
    <div
      className={`
        relative overflow-hidden rounded-lg border backdrop-blur-sm
        ${isActive ? 'border-blue-500/50' : 'border-gray-700'}
        ${isUpdating ? 'animate-pulse-glow' : ''}
        bg-gray-800/50 p-4 transition-all hover:border-gray-500
      `}
    >
      {/* Loading Overlay */}
      {isUpdating && (
        <div className="absolute inset-0 flex items-center justify-center bg-gray-900/50 backdrop-blur-sm">
          <LoadingSpinner size="sm" color="text-blue-400" />
        </div>
      )}

      {/* Token Info */}
      <div className="flex items-start justify-between">
        <div className="flex items-center space-x-3">
          <div className="relative">
            <div className="h-10 w-10 rounded-full bg-gray-700 flex items-center justify-center">
              <span className="text-lg font-bold">{symbol.slice(0, 2)}</span>
            </div>
            {isActive && (
              <div className="absolute -bottom-1 -right-1 h-3 w-3 rounded-full bg-blue-500 animate-pulse">
                <div className="absolute inset-0 rounded-full bg-blue-500 animate-ping" />
              </div>
            )}
          </div>
          <div>
            <h3 className="font-semibold">{symbol}</h3>
            <p className="text-xs text-gray-400">{`${address.slice(0, 6)}...${address.slice(-4)}`}</p>
          </div>
        </div>

        {/* Profit Display */}
        {lastOpportunity && showProfit && (
          <div
            className={`
              flex flex-col items-end space-y-1
              animate-slide-in
            `}
          >
            <span className="text-sm font-medium text-green-400">
              +{lastOpportunity.profit.toFixed(4)}%
            </span>
            <span className="text-xs text-gray-400">
              {new Date(lastOpportunity.timestamp).toLocaleTimeString()}
            </span>
          </div>
        )}
      </div>

      {/* DEX Info and Stats */}
      <div className="mt-4 space-y-2">
        <div className="flex items-center justify-between text-sm">
          <span className="text-gray-400">Decimals</span>
          <span>{decimals}</span>
        </div>

        {lastOpportunity && (
          <div className="flex items-center justify-between text-sm">
            <span className="text-gray-400">Last Opportunity</span>
            <span className="text-green-400">
              {new Date(lastOpportunity.timestamp).toLocaleString()}
            </span>
          </div>
        )}

        {/* Token Actions */}
        <div className="flex justify-end space-x-2 mt-4">
          <button
            className={`
              px-3 py-1.5 text-sm font-medium rounded-md
              transition-colors duration-200
              ${isActive
                ? 'bg-blue-500/20 text-blue-400 hover:bg-blue-500/30'
                : 'bg-gray-700/50 text-gray-300 hover:bg-gray-700/70'
              }
            `}
          >
            View Details
          </button>
        </div>
      </div>
    </div>
  );
};

export default TokenCard;
