import React from 'react';
import { ArrowRight, TrendingUp, Activity } from 'lucide-react';

interface TokenCardProps {
  symbol: string;
  address: string;
  decimals: number;
  lastOpportunity?: {
    profit: number;
    timestamp: number;
  };
  isActive: boolean;
}

export const TokenCard: React.FC<TokenCardProps> = ({
  symbol,
  address,
  decimals,
  lastOpportunity,
  isActive,
}) => {
  return (
    <div className={`
      relative overflow-hidden rounded-xl border
      ${isActive ? 'border-blue-500 bg-blue-500/5' : 'border-gray-700 bg-gray-800/50'}
      transition-all duration-300 hover:border-blue-400/50 hover:shadow-lg
      backdrop-blur-sm p-4
    `}>
      {isActive && (
        <div className="absolute top-2 right-2">
          <Activity className="w-4 h-4 text-blue-400 animate-pulse" />
        </div>
      )}

      <div className="flex items-center space-x-3">
        <div className="w-10 h-10 rounded-full bg-gray-700 flex items-center justify-center">
          <span className="text-lg font-bold">{symbol.slice(0, 2)}</span>
        </div>
        <div>
          <h3 className="font-semibold text-lg">{symbol}</h3>
          <p className="text-sm text-gray-400 truncate w-32" title={address}>
            {address.slice(0, 6)}...{address.slice(-4)}
          </p>
        </div>
      </div>

      <div className="mt-4 space-y-2">
        <div className="flex items-center justify-between text-sm">
          <span className="text-gray-400">Decimals</span>
          <span className="font-mono">{decimals}</span>
        </div>

        {lastOpportunity && (
          <>
            <div className="flex items-center justify-between text-sm">
              <span className="text-gray-400">Last Profit</span>
              <div className="flex items-center text-green-400">
                <TrendingUp className="w-3 h-3 mr-1" />
                {lastOpportunity.profit.toFixed(4)}%
              </div>
            </div>
            <div className="flex items-center justify-between text-sm">
              <span className="text-gray-400">Last Seen</span>
              <span className="text-gray-300">
                {new Date(lastOpportunity.timestamp).toLocaleTimeString()}
              </span>
            </div>
          </>
        )}
      </div>

      <button className="mt-4 w-full py-2 px-4 rounded-lg bg-blue-500/10 hover:bg-blue-500/20 
        text-blue-400 font-medium flex items-center justify-center space-x-2 transition-colors">
        <span>View Details</span>
        <ArrowRight className="w-4 h-4" />
      </button>
    </div>
  );
};

export default TokenCard;
