import React from 'react';
import { ArbitrageOpportunity } from '../types';

interface ArbitrageTableProps {
  opportunities: ArbitrageOpportunity[];
}

function ArbitrageTable({ opportunities }: ArbitrageTableProps) {
  return (
    <div className="overflow-x-auto">
      <table className="min-w-full divide-y divide-gray-200">
        <thead className="bg-gray-50">
          <tr>
            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Route</th>
            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Profit</th>
            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">DEXs</th>
            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Flash Loan Amount</th>
            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Profit Percentage</th>
            <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">Timestamp</th>
          </tr>
        </thead>
        <tbody className="bg-white divide-y divide-gray-200">
          {opportunities.map((opportunity) => (
            <tr key={opportunity.timestamp}>
              <td className="px-6 py-4 whitespace-nowrap">{opportunity.route}</td>
              <td className="px-6 py-4 whitespace-nowrap">{opportunity.profit.toFixed(6)}</td>
              <td className="px-6 py-4 whitespace-nowrap">{opportunity.steps.map(step => step.dex).join(' -> ')}</td>
              <td className="px-6 py-4 whitespace-nowrap">{opportunity.flashLoanAmount}</td>
              <td className="px-6 py-4 whitespace-nowrap">{opportunity.profitPercentage.toFixed(2)}%</td>
              <td className="px-6 py-4 whitespace-nowrap">{new Date(opportunity.timestamp).toLocaleString()}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ArbitrageTable;
