import React, { useState, useEffect, useRef } from 'react';
import { ArbitrageOpportunity, LogEntry } from './types';
import { ArrowRightLeft, RefreshCcw, Menu, X, LayoutGrid, List, Settings } from 'lucide-react';
import TokenCard from './components/TokenCard';
import LogsPanel from './components/LogsPanel';
import ToastContainer, { ToastData } from './components/ToastContainer';
import LoadingSpinner from './components/LoadingSpinner';

interface Token {
  symbol: string;
  address: string;
  decimals: number;
  lastOpportunity?: {
    profit: number;
    timestamp: number;
  };
}

const TOKENS: { [key: string]: Token } = {
  USDC: { symbol: 'USDC', address: '0x2791Bca1f2de4661ED88A30C99A7a9449Aa84174', decimals: 6 },
  WETH: { symbol: 'WETH', address: '0x7ceB23fD6bC0adD59E62ac25578270cFf1b9f619', decimals: 18 },
  DAI: { symbol: 'DAI', address: '0x8f3Cf7ad23Cd3CaDbD9735AFf958023239c6A063', decimals: 18 },
  WMATIC: { symbol: 'WMATIC', address: '0x0d500B1d8E8eF31E21C99d1Db9A6444d3ADf1270', decimals: 18 },
  WBTC: { symbol: 'WBTC', address: '0x1BFD67037B42Cf73acF2047067bd4F2C47D9BfD6', decimals: 8 },
  AAVE: { symbol: 'AAVE', address: '0xD6DF932A45C0f255f85145f286eA0b292B21C90B', decimals: 18 },
  LINK: { symbol: 'LINK', address: '0x53E0bca35eC356BD5ddDFebbD1Fc0fD03FaBad39', decimals: 18 },
  CRV: { symbol: 'CRV', address: '0x172370d5Cd63279eFa6d502DAB29171933a610AF', decimals: 18 },
  SUSHI: { symbol: 'SUSHI', address: '0x0b3F868E0BE5597D5DB7fEB59E1CADBb0fdDa50a', decimals: 18 },
  DPI: { symbol: 'DPI', address: '0x85955046DF4668e1DD369D2DE9f3AEB98DD2A369', decimals: 18 }
};

function App() {
  const [opportunities, setOpportunities] = useState<ArbitrageOpportunity[]>([]);
  const [logs, setLogs] = useState<LogEntry[]>([]);
  const [lastUpdate, setLastUpdate] = useState<Date>(new Date());
  const [isConnected, setIsConnected] = useState(false);
  const [toasts, setToasts] = useState<ToastData[]>([]);
  const [isLoading, setIsLoading] = useState(true);
  const [viewMode, setViewMode] = useState<'grid' | 'list'>('grid');
  const [isSidebarOpen, setIsSidebarOpen] = useState(true);
  const wsRef = useRef<WebSocket | null>(null);

  // New state to handle arbitrage popup details
  const [arbitragePopup, setArbitragePopup] = useState<ArbitrageOpportunity | null>(null);

  const [tokenStates, setTokenStates] = useState<{ [key: string]: Token }>(() => {
    return Object.entries(TOKENS).reduce((acc, [symbol, token]) => ({
      ...acc,
      [symbol]: { ...token }
    }), {});
  });

  // Function to show toast notifications
  const showToast = (toast: Omit<ToastData, 'id'>) => {
    const id = Date.now().toString();
    setToasts(prev => [...prev, { ...toast, id }]);
  };

  const removeToast = (id: string) => {
    setToasts(prev => prev.filter(toast => toast.id !== id));
  };

  // Update token state with opportunity data
  const updateTokenOpportunity = (opportunity: ArbitrageOpportunity) => {
    const fromToken = opportunity.steps[0].from;
    if (tokenStates[fromToken]) {
      setTokenStates(prev => ({
        ...prev,
        [fromToken]: {
          ...prev[fromToken],
          lastOpportunity: {
            profit: opportunity.profitPercentage,
            timestamp: opportunity.timestamp
          }
        }
      }));
    }
  };

  // WebSocket setup: Connect to server and handle logs and arbitrage opportunities.
  useEffect(() => {
    const ws = new WebSocket('ws://localhost:3003');
    ws.onopen = () => {
      setIsConnected(true);
      setIsLoading(false);
      showToast({
        type: 'success',
        title: 'WebSocket Connected',
        message: 'Connected to server successfully.'
      });
    };

    ws.onmessage = (event) => {
      try {
        console.log("WebSocket message received:", event.data);
        const message = JSON.parse(event.data);

        // Process logs
        if (message.type === 'log' || message.type === 'logs') {
          if (message.data) {
            if (Array.isArray(message.data)) {
              setLogs(message.data);
            } else if (message.data.logs && Array.isArray(message.data.logs)) {
              setLogs(message.data.logs);
            } else {
              setLogs(prev => [message.data, ...prev]);
            }
            setLastUpdate(new Date());
          }
        }
        // Fallback: if message is a single log entry without type
        else if (!message.type && message.timestamp && message.message) {
          setLogs(prev => [message, ...prev]);
          setLastUpdate(new Date());
        }
        // Process initial bulk logs data
        else if (message.type === 'initial' && message.data) {
          if (message.data.logs && Array.isArray(message.data.logs)) {
            setLogs(message.data.logs);
          } else {
            setLogs(message.data);
          }
          setLastUpdate(new Date());
        }
        // Process arbitrage opportunities
        else if (message.type === 'opportunity' && message.data) {
          const opp = message.data as ArbitrageOpportunity;
          setOpportunities(prev => [opp, ...prev]);
          updateTokenOpportunity(opp);
          // Show popup with arbitrage details
          setArbitragePopup(opp);
          // Auto close the popup after 10 seconds
          setTimeout(() => {
            setArbitragePopup(null);
          }, 10000);
        }
      } catch (err) {
        console.error('Error parsing WebSocket message:', err);
        // Log the error but do not block further processing
        showToast({
          type: 'error',
          title: 'Processing Error',
          message: 'An error occurred while processing a message.'
        });
      }
    };

    ws.onclose = () => {
      setIsConnected(false);
      showToast({
        type: 'error',
        title: 'WebSocket Disconnected',
        message: 'Connection to server lost.'
      });
    };

    ws.onerror = (error) => {
      console.error('WebSocket error:', error);
      showToast({
        type: 'error',
        title: 'WebSocket Error',
        message: 'Error occurred in WebSocket connection.'
      });
    };

    wsRef.current = ws;
    return () => {
      ws.close();
    };
  }, []);

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-900 to-gray-800 text-white">
      {/* Arbitrage Popup */}
      {arbitragePopup && (
        <div className="fixed top-0 left-0 right-0 bottom-0 flex items-center justify-center bg-black bg-opacity-50 z-50">
          <div className="bg-gray-800 p-6 rounded-lg shadow-lg max-w-lg w-full">
            <h2 className="text-xl font-bold mb-4">Detalhes da Arbitragem</h2>
            <div className="text-sm space-y-2">
              <p><strong>Data/Hora:</strong> {new Date(arbitragePopup.timestamp).toLocaleString()}</p>
              <p><strong>Lucro:</strong> {arbitragePopup.profitPercentage.toFixed(4)}%</p>
              {arbitragePopup.steps && arbitragePopup.steps.map((step, index) => (
                <div key={index} className="border-t border-gray-700 pt-2">
                  <p><strong>DEX:</strong> {(step as any).dexName || 'N/D'}</p>
                  <p><strong>Pre√ßo:</strong> {(step as any).price || 'N/D'}</p>
                  <p><strong>Custo:</strong> {(step as any).cost || 'N/D'}</p>
                  <p><strong>De:</strong> {step.from}</p>
                  <p><strong>Para:</strong> {step.to}</p>
                </div>
              ))}
            </div>
          </div>
        </div>
      )}

      {/* Header */}
      <header className="bg-gray-800/50 border-b border-gray-700 sticky top-0 z-10">
        <div className="container mx-auto px-4 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center space-x-3">
              <button
                onClick={() => setIsSidebarOpen(!isSidebarOpen)}
                className="p-2 hover:bg-gray-700/50 rounded-lg transition-colors"
              >
                {isSidebarOpen ? <X className="w-6 h-6" /> : <Menu className="w-6 h-6" />}
              </button>
              <ArrowRightLeft className="w-8 h-8 text-blue-400" />
              <h1 className="text-2xl font-bold">Polygon Arbitrage Scanner</h1>
            </div>

            <div className="flex items-center space-x-4">
              {/* Connection Status */}
              <div className="flex items-center space-x-2">
                <div
                  className={`h-3 w-3 rounded-full ${isConnected ? 'bg-green-500' : 'bg-red-500'}`}
                />
                <span className="text-sm text-gray-400">
                  {isConnected ? 'Connected' : 'Disconnected'}
                </span>
              </div>

              {/* Last Update */}
              <div className="flex items-center space-x-2 text-sm text-gray-400">
                <RefreshCcw className={`w-4 h-4 ${isConnected ? 'animate-spin' : ''}`} />
                <span>Last update: {lastUpdate.toLocaleTimeString()}</span>
              </div>

              {/* View Mode Toggle */}
              <div className="flex items-center space-x-2 bg-gray-700/30 rounded-lg p-1">
                <button
                  onClick={() => setViewMode('grid')}
                  className={`p-2 rounded-md transition-colors ${
                    viewMode === 'grid' ? 'bg-blue-500/20 text-blue-400' : 'text-gray-400'
                  }`}
                >
                  <LayoutGrid className="w-4 h-4" />
                </button>
                <button
                  onClick={() => setViewMode('list')}
                  className={`p-2 rounded-md transition-colors ${
                    viewMode === 'list' ? 'bg-blue-500/20 text-blue-400' : 'text-gray-400'
                  }`}
                >
                  <List className="w-4 h-4" />
                </button>
              </div>

              {/* Settings */}
              <button className="p-2 hover:bg-gray-700/50 rounded-lg transition-colors">
                <Settings className="w-5 h-5 text-gray-400" />
              </button>
            </div>
          </div>
        </div>
      </header>

      {/* Main Content */}
      <div className="container mx-auto px-4 py-8">
        <div className="flex space-x-6">
          {/* Token Grid/List */}
          <div className={`flex-1 transition-all duration-300 ${isSidebarOpen ? 'w-2/3' : 'w-full'}`}>
            {isLoading ? (
              <div className="flex items-center justify-center h-64">
                <LoadingSpinner size="lg" />
              </div>
            ) : viewMode === 'grid' ? (
              <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
                {Object.values(tokenStates).map((token) => (
                  <TokenCard
                    key={token.symbol}
                    {...token}
                    isActive={opportunities.some(opp =>
                      opp.steps[0].from === token.symbol ||
                      opp.steps[0].to === token.symbol
                    )}
                  />
                ))}
              </div>
            ) : (
              <div className="space-y-2">
                {Object.values(tokenStates).map((token) => (
                  <div
                    key={token.symbol}
                    className="flex items-center justify-between p-4 bg-gray-800/50 rounded-lg border border-gray-700"
                  >
                    <div className="flex items-center space-x-4">
                      <div className="w-10 h-10 rounded-full bg-gray-700 flex items-center justify-center">
                        <span className="text-lg font-bold">{token.symbol.slice(0, 2)}</span>
                      </div>
                      <div>
                        <h3 className="font-semibold">{token.symbol}</h3>
                        <p className="text-sm text-gray-400">{token.address.slice(0, 8)}...</p>
                      </div>
                    </div>
                    {token.lastOpportunity && (
                      <div className="text-green-400">
                        +{token.lastOpportunity.profit.toFixed(4)}%
                      </div>
                    )}
                  </div>
                ))}
              </div>
            )}
          </div>

          {/* Logs Panel */}
          {isSidebarOpen && (
            <div className="w-1/3">
              <LogsPanel logs={logs} />
            </div>
          )}
        </div>
      </div>

      {/* Toasts */}
      <ToastContainer toasts={toasts} onRemove={removeToast} />
    </div>
  );
}

export default App;
