import React, { useState, useEffect } from 'react';
import axios from 'axios';
import { ArbitrageOpportunity } from './types';
import ArbitrageTable from './components/ArbitrageTable';

function App() {
  const [opportunities, setOpportunities] = useState<ArbitrageOpportunity[]>([]);

  useEffect(() => {
    const fetchOpportunities = async () => {
      try {
        const response = await axios.get('http://localhost:3000/api/opportunities');
        setOpportunities(response.data);
      } catch (error) {
        console.error('Error fetching opportunities:', error);
      }
    };

    fetchOpportunities();
    const intervalId = setInterval(fetchOpportunities, 5000);

    return () => clearInterval(intervalId);
  }, []);

  return (
    <div className="container mx-auto p-4">
      <h1 className="text-2xl font-bold mb-4">Arbitrage Opportunities</h1>
      <ArbitrageTable opportunities={opportunities} />
    </div>
  );
}

export default App;
